    <Layout>
        This is next js
        <Prices bpi={props.bpi}/>
        {props.bpi.USD.description}
    </Layout>
)

Index.getInitialProps=async function(){
    const res=await fetch('https://api.coindesk.com/v1/bpi/currentprice.json');

    const data=await res.json();

    return {
        bpi: data
    }
}

export default Index
